mapper.insert:
insert into uorder (UID, MID,
CID, OrderDate, Num, Statu)
values (#{uid}, #{mid}, #{cid}, #{orderdate}, #{num}, #{statu});

mapper.select:
select uorder.OID, uorder.MID, uorder.UID, uorder.CID, product.Pname, user.Username, uorder.Num, product.Price * uorder.Num as total, uorder.OrderDate, uorder.Statu from uorder left join user on uorder.UID = user.UID left join product on uorder.CID = product.PID
and uorder.OID = #{oid}
and product.Pname like '%${pname}%'
and user.Username like '%${mname}%'
and user.Username like '%${username}%'
and uorder.Num >= #{num_min}
and uorder.Num <= #{num_max}
and uorder.OrderDate >= #{start}
and uorder.OrderDate <= #{end}
and uorder.Statu = #{statu}

select Username
from user
where user.UID = #{mid}
